import React, {useState} from "react";
import { useForm, ValidationError } from '@formspree/react';


function Footer() {
    const [state, handleSubmit] = useForm("xeqyllby");
    if (state.succeeded) {
        return <p>Дякую,очікуйте дзвінок про відправлення!</p>;
    }
    return (
        <form onSubmit={handleSubmit}>
        <label htmlFor="email">
          Email Address
        </label>
        <input
          id="email"
          type="email" 
          name="email"
        />
        <ValidationError 
          prefix="Email" 
          field="email"
          errors={state.errors}
        />
        <textarea
          id="message"
          name="message"
        />
        <ValidationError 
          prefix="Message" 
          field="message"
          errors={state.errors}
        />
        <button type="submit" disabled={state.submitting}>
          Submit
        </button>
      </form>
    );
  }


export default Footer
