import './App.css';
import React ,{ useState} from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Navbar from './Components/Common/Header/Navbar';
import HomePage from './Components/HomePage/HomePage';
import Sponsored from './Components/Sponsored/Sponsored';
import Content from './Components/Common/Content/Content'
import Footer from './Components/Common/Footer/Footer';
import Items from './Components/AboutPage/Items/Items';


function App() {
  const [showItems, setShowItems] = useState(false);

  return (
    <Router>
    <div className="App">
      <Navbar setShowItems={setShowItems} />
      <Routes>
        {/* Render all components on the main page */}
        <Route path="/" element={<HomePage/> && + <Sponsored/>} />
        <Route path="/contacts" element={<Footer />} />
        <Route path="/" element={<Navbar />} />
        <Route path="/" element={<Content />} />
        {/* Conditionally render the Items component */}
        {showItems && <Route path="/items" element={<Items />} />}
        {/* Redirect to /items if URL is /items */}
        <Route path="/items" element={<Navigate to="/items" />} />

      </Routes>
      <Footer />
    </div>
  </Router>
  );
}

export default App;
