import './App.css';
import Navbar from './Components/Common/Header/Navbar';
import HomePage from './Components/HomePage/HomePage';
import Sponsored from './Components/Sponsored/Sponsored';
import Content from './Components/Common/Content/Content'
import Footer from './Components/Common/Footer/Footer';


const express = require('express');
const nodemailer = require('nodemailer');
const app = express();
app.use(express.json());

const transporter = nodemailer.createTransport({
  service: 'gmail', // For example, using Gmail
  auth: {
    user: 'bugs.20.bunny.20@gmail.com',
    pass: 'Voloshyn2002', // Consider using environment variables or OAuth2 for better security
  },
});

app.post('/send-email', (req, res) => {
  const { email, message } = req.body;
  const mailOptions = {
    from: email,
    to: 'bugs.20.bunny.20@gmail.com', // Your email where you want to receive messages
    subject: 'Message from your site',
    text: message,
  };

  transporter.sendMail(mailOptions, (error, info) => {
    if (error) {
      console.log(error);
      res.send('error'); // or use res.status(500).send() for a HTTP status code
    } else {
      console.log('Email sent: ' + info.response);
      res.send('sent');
    }
  });
});

const PORT = process.env.PORT || 3001;
app.listen(PORT, () => {
  console.log(`Server running on port ${PORT}`);
});





function App() {
  return (
    <div className="App">
    <Navbar/>
    <HomePage/>
    <Sponsored/>
    <Content/>
    <Footer/>
    </div>
  );
}

export default App;
